    async function createOrder(side, price, quantity, itemMint, currencyMint = atlasProgramId) {
        let instructions = [];

        const receiveMint = side === 'buy' ? itemMint : currencyMint;
        const depositMint = side === 'buy' ? currencyMint : itemMint;

        let depositTokenAccount = await solanaConnection.getParsedTokenAccountsByOwner(userPublicKey, {mint: new solanaWeb3.PublicKey(depositMint)});
        //const depositTokenAccount = await solanaConnection.getTokenAccount(userPublicKey, depositMint);
        //await createPDA(depositTokenAccount, userPublicKey, depositMint)

        const receiveTokenAccount = await solanaConnection.getParsedTokenAccountsByOwner(userPublicKey, {mint: new solanaWeb3.PublicKey(receiveMint)});
        //const receiveTokenAccount = await solanaConnection.getTokenAccount(userPublicKey, receiveMint);
        //await createPDA(receiveTokenAccount, userPublicKey, receiveMint)

        const initializerDepositTokenAccount = 'publicKey' in depositTokenAccount ? depositTokenAccount.publicKey : depositTokenAccount.value[0].pubkey;

        const initializerReceiveTokenAccount = 'publicKey' in receiveTokenAccount ? receiveTokenAccount.publicKey : receiveTokenAccount.value[0].pubkey;

        const orderAccount = solanaWeb3.Keypair.generate();
        let createOrderInstruction = await createAccountInstruction(marketProgram, orderAccount);
        instructions.push(createOrderInstruction);

        const [orderVaultAccount] = await BrowserAnchor.anchor.web3.PublicKey.findProgramAddressSync(
            [
                BrowserBuffer.Buffer.Buffer.from('order-vault-account'),
                userPublicKey.toBuffer(),
                new solanaWeb3.PublicKey(depositMint).toBuffer(),
            ],
            marketProgramId,
        );

        const [orderVaultAuthority] = await BrowserAnchor.anchor.web3.PublicKey.findProgramAddressSync(
            [
                BrowserBuffer.Buffer.Buffer.from('order-vault-auth'),
                userPublicKey.toBuffer()
            ],
            marketProgramId,
        );

        let [marketVarsAccount] = await BrowserAnchor.anchor.web3.PublicKey.findProgramAddressSync(
            [
                BrowserBuffer.Buffer.Buffer.from('market-vars')
            ],
            marketProgramId,
        );

        let [registeredCurrency] = await BrowserAnchor.anchor.web3.PublicKey.findProgramAddressSync(
            [
                BrowserBuffer.Buffer.Buffer.from('registered-currency'),
                new solanaWeb3.PublicKey(currencyMint).toBuffer()
            ],
            marketProgramId,
        );

        let [openOrdersCounter] = await BrowserAnchor.anchor.web3.PublicKey.findProgramAddressSync(
            [
                BrowserBuffer.Buffer.Buffer.from('open-orders-counter'),
                userPublicKey.toBuffer(),
                new solanaWeb3.PublicKey(depositMint).toBuffer(),
            ],
            marketProgramId,
        );

        const openOrdersCounterAcct = await solanaConnection.getAccountInfo(openOrdersCounter);

        if (openOrdersCounterAcct === null) {
            let initializeOpenOrdersCounterIx = { instruction: await marketProgram.methods.initializeOpenOrdersCounter()
                                                 .accountsStrict({
                                                     payer: userPublicKey,
                                                     user: userPublicKey,
                                                     openOrdersCounter: openOrdersCounter,
                                                     depositMint,
                                                     systemProgram: systemProgramId,
                                                 }).instruction()};
            instructions.push(initializeOpenOrdersCounterIx);
        }

        const programMethod = side === 'buy' ? marketProgram.methods.processInitializeBuy : marketProgram.methods.processInitializeSell;

        let instruction = { instruction: await programMethod(new BrowserAnchor.anchor.BN(price), new BrowserAnchor.anchor.BN(quantity))
                           .accountsStrict({
                               orderVaultAccount: orderVaultAccount,
                               orderVaultAuthority,
                               orderInitializer: userPublicKey,
                               marketVarsAccount,
                               depositMint: depositMint,
                               receiveMint: receiveMint,
                               initializerDepositTokenAccount,
                               initializerReceiveTokenAccount,
                               orderAccount: orderAccount.publicKey,
                               registeredCurrency,
                               openOrdersCounter,
                               systemProgram: systemProgramId,
                               rent: rentProgramId,
                               tokenProgram: tokenProgramId,
                           }).instruction()};

        instructions.push(instruction);
        let txResult = await txSignAndSend(instructions, orderAccount);
        return {txResult: txResult, orderAccount: orderAccount};
    }

