    async function getBuildOrder(orderAccount, itemMint) {
        let orderSignatureRecords = await solanaConnection.getSignaturesForAddress(new solanaWeb3.PublicKey(orderAccount));
        let orderSignatures = orderSignatureRecords.map(record => record.signature);
        let orderTransactions = await solanaConnection.getParsedTransactions(orderSignatures, {maxSupportedTransactionVersion: 1});
        //let orderFillTransactions = orderTransactions.filter(tx => tx.meta.innerInstructions[0].instructions.find(ix => ix.parsed.info.mint == itemMint));
        let orderFillTransactions = orderTransactions.map(tx => tx.meta.innerInstructions[0].instructions.find(ix => ix.parsed.info.mint == itemMint)).filter(Boolean);
        let parsedContributors = orderFillTransactions.map(tx => ({contributor: tx.parsed.info.authority, itemMint: tx.parsed.info.mint, amount: tx.parsed.info.tokenAmount.uiAmount}));
        return parsedContributors;
    }

    async function calcDistribution(orderId) {
        let buildOrders = await getStoredValue('BuildOrders');
        let storedOrder = buildOrders[orderId];
        let totalContributionValue = storedOrder.orders.reduce((partialSum, a) => partialSum + (a.amount * a.price), 0);
        let totalContributions = [];
        for (const order of storedOrder.orders) {
            let ingredientItem = cargoItems.find(item => item.name === order.item);
            let contributions = await getBuildOrder(order.account, ingredientItem.token);
            for (const contribution of contributions) {
                let totalSellPrice = storedOrder.sellPrice * storedOrder.amount
                let distibutionAmount = totalSellPrice * ((order.price * contribution.amount) / totalContributionValue);
                totalContributions.push({contributor: contribution.contributor, distibutionAmount: distibutionAmount});
            }
        }
        return totalContributions;
    }

